function login() {
    var valid;  
    valid = checkContents();
    if(valid) {
        $('#loading').show();
        jQuery.ajax({
            url: "https://joboy.in/home/login_home",
            data:'username='+$("#username").val()+
            '&password='+$("#password").val(),
            type: "POST",
            success:function(data){
              if(data==0){
                $("#login-status").addClass("alert alert-danger");
                $("#login-status").html('Login Failed.Try Again.');
                $('#loading').hide();
              }else{
                $("#login-status").addClass("alert alert-success");
                $("#login-status").html('Successfully Logged In. Please wait...');
                $('#loading').hide();
                //$('#myModal').modal('hide');
                location.reload();
              }
                //$("#login-status").html(data);
                $('#username').val('');
                $('#password').val('');
                  
                //$('#myModal').modal('hide');
            },
            error:function (data){
                $("#login-status").html(data);
                $('#loading').hide();
                //$('#myModal').modal('hide');  
            }
        });
    }
}

function checkContents() {
     var valid = true; 
   // $(".form-control").css('background-color','');
    $(".info").html('');
    if(!$("#username").val()) {
        $("#username-info").html("*Username Required");
        //$("#userName").css('background-color','#FFFFDF');
        valid = false;
    }
    if(!$("#password").val()) {
        $("#password-info").html("*Password Required");
        //$("#userName").css('background-color','#FFFFDF');
        valid = false;
    }
    return valid;
}



