      $(".over_lay").hide();
         if(result==0) {
          valid = false;
          //reg_otp_pop_div.addClass('has-error');
          $('#spOtp1-info').html("*Invaild OTP");
          $('#spOtp1').attr("data-item-set","1");
          eo.value = '1';
